import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { connect } from "react-redux";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ngayDi: new Date(),
      idBenDi: 0,
      idBenDen: 0,
      idBenDenCoTheDen: [],
    };
    this.handleDateChange = this.handleDateChange.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // bắt sự kiện thay đổi ngày
  handleDateChange = (date) => {
    this.setState({
      ngayDi: date,
    });
  };

  //bắt sự kiện thay đổi tuyến đi, tuyến đến
  handleChange(event) {
    this.setState({
      [event.target.name]: parseInt(event.target.value),
    });
  }

  // sự kiện nhấn button submit
  handleSubmit(event) {
    alert("Bạn vừa nhấn nút Submit ");
    event.preventDefault();
  }
  render() {
    // if (this.state.idBenDi === 0) {
    //   console.log("Chưa chọn bến đi");
    // } else {
    //   console.log(
    //     "Đã cập nhật bến đi ID ben di khach hang chon:" + this.state.idBenDi
    //   );
    //   this.props.dstuyenData.map((item, index) => {
    //     if (this.state.idBenDi === item.idBenDi) {
    //       console.log(item.TenTuyen);
    //       // console.log(this.state.idBenDenCoTheDen);
    //       this.setState({
    //         idBenDenCoTheDen: [...this.state.idBenDenCoTheDen, item.idBenDen],
    //       });
    //     }
    //   });
    // }

    return (
      <div>
        <div id="booking" className="section">
          <div className="section-center">
            <div className="container">
              <div className="row">
                <div className="booking-form z-indept-2">
                  <form>
                    <div className="row">
                      <div className="col-md-4">
                        <div className="form-group bmd-form-group">
                          <select
                            className="form-control selectpicker"
                            data-style="btn btn-link"
                            id="benDi"
                            name="idBenDi"
                            onChange={(event) => this.handleChange(event)}
                          >
                            <option value={this.state.idBenDi}>
                              Chọn điểm đi
                            </option>
                            {this.props.dsbenData.map((item, index) => {
                              return (
                                <option key={index} value={item.id}>
                                  {item.TenBen}
                                </option>
                              );
                            })}
                          </select>
                        </div>
                      </div>
                      <div className="col-md-4">
                        <div className="form-group bmd-form-group">
                          <select
                            onChange={(event) => this.handleChange(event)}
                            className="form-control selectpicker"
                            data-style="btn btn-link"
                            id="benDen"
                            name="idBenDen"
                          >
                            <option value={this.state.idBenDen}>
                              Chọn điểm đến
                            </option>
                            <option>{this.state.idBenDenCoTheDen}</option>
                          </select>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="form-group bmd-form-group">
                          <DatePicker
