import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { connect } from "react-redux";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ngayDi: new Date(),
      idBenDi: 0,
    };
    this.handleDateChange = this.handleDateChange.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleDateChange = (date) => {
    this.setState({
      ngayDi: date,
    });
    console.log(date);
  };

  handleChange(event) {
    console.log(event.target.value);
  }
  handleSubmit(event) {
    alert("Bạn vừa nhấn nút Submit ");
    event.preventDefault();
  }
  render() {
    // in danh sách bến ra để test
    // console.log(this.props.dsbenData);
    console.log(this.state);
    return (
      <div>
        <div id="booking" className="section">
          <div className="section-center">
            <div className="container">
              <div className="row">
                <div className="booking-form z-indept-2">
                  <form>
                    <div className="row">
                      <div className="col-md-4">
                        <div className="form-group bmd-form-group">
                          <select
                            className="form-control selectpicker"
                            data-style="btn btn-link"
                            id="exampleFormControlSelect1"
                            onChange={(event) => this.handleChange(event)}
                          >
                            <option value={this.state.idBenDi}>
                              Chọn điểm đi
                            </option>
                            {this.props.dsbenData.map((item, index) => {
                              return (
                                <option key={index} value={item.id}>
                                  {item.TenBen}
                                </option>
                              );
                            })}
                          </select>
                        </div>
                      </div>
                      <div className="col-md-4">
                        <div className="form-group bmd-form-group">
                          <select
                            onChange={(event) => this.handleChange(event)}
                            className="form-control selectpicker"
                            data-style="btn btn-link"
                            id="exampleFormControlSelect1"
                          >
                            <option value={this.state.idBenDen}>
                              Chọn điểm đến
                            </option>
                          </select>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="form-group bmd-form-group">
                          {/* <input
                            className="form-control"
                            type="date"
                            required
                          /> */}
                          <DatePicker
                            name="date"
                            className="form-control"
                            selected={this.state.ngayDi}
                            onChange={this.handleDateChange}
                            minDate={new Date()}
                            name="startDate"
                            dateFormat="MM/dd/yyyy"
                          />
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="form-btn">
                          <button
                            onClick={(event) => this.handleSubmit(event)}
                            className="btn btn-warning btn-block btn-lg waves-effect waves-light z-indept-1"
                          >
                            Mua vé
                          </button>
                        </div>
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* end section search */}
        <div className="cd-section" id="projects">
          {/*     *********    PROJECTS 1     *********      */}
          <div className="projects-1" id="projects-1">
            <div className="container">
              <div className="row">
                <div className="col-md-8 ml-auto mr-auto text-center">
                  <h2 className="title">Some of Our Awesome Products - 1</h2>
                  <ul className="nav nav-pills nav-pills-rose">
                    <li className="nav-item">
                      <a
                        className="nav-link active"
                        href="#pill1"
                        data-toggle="tab"
                      >
                        All
                      </a>
                    </li>
                    <li className="nav-item">
                      <a className="nav-link" href="#pill2" data-toggle="tab">
                        Marketing
                      </a>
                    </li>
                    <li className="nav-item">
                      <a className="nav-link" href="#pill3" data-toggle="tab">
                        Development
                      </a>
                    </li>
                    <li className="nav-item">
                      <a className="nav-link" href="#pill3" data-toggle="tab">
                        Productivity
                      </a>
                    </li>
                    <li className="nav-item">
                      <a className="nav-link" href="#pill3" data-toggle="tab">
                        Web Design
                      </a>
                    </li>
                  </ul>
                  <div className="tab-content tab-space">
                    <div className="tab-pane active" id="pill1" />
                    <div className="tab-pane" id="pill2" />
                    <div className="tab-pane" id="pill3" />
                  </div>
                </div>
